package com.cxg.gtd.controller;import com.cxg.gtd.entity.CollectionThings;import com.cxg.gtd.entity.User;import com.cxg.gtd.service.CollectionsService;import io.swagger.annotations.ApiOperation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.util.List;@RestController@RequestMapping("/gtd")public class Index {    @Autowired    CollectionsService collectionService ;    /**     * 初始页面     * @param user     * @param request     * @param response     * @return     * @throws Exception     */    @RequestMapping("/index")    public String  index(User user, HttpServletRequest request,                         HttpServletResponse response ) throws Exception{        response.sendRedirect(request.getContextPath() + "/index.html");        return null;    }    /**     * 当前登录人的待办任务列表     * @return     */    @ApiOperation("首页收件箱列表")    @GetMapping("/cList")    public List<CollectionThings> getCollectionThings(){        User user = new User();        user.setUserId("cxg");        return collectionService.getCollectionThings(user);    }}